import React, { Component } from 'react';
// import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Redirect } from 'react-router-dom';
import { NavLink } from 'react-router-dom';

// import Modal from '../components/Modal/Modal';
// import Backdrop from '../components/Backdrop/Backdrop';
// import UserList from '../components/Users/UserList/UserList';
// import UserDetail from '../components/Users/UserDetail';
// import Spinner from '../components/Spinner/Spinner';
// import AuthContext from '../context/auth-context';

import CreateUserForm from '../components/Forms/CreateUserForm';
// import UpdateUserForm from '../components/Forms/UpdateUserForm';
import './Users.css';

class SignupPage extends Component {
  // state = {
  //   success: "Signup!!",
  //   failure: false,
  //   neutral: true
  // };
  // isActive = true;

  // static contextType = AuthContext;

  constructor(props) {
    super(props);
    this.success = 'Signup';
  }
  //
  // componentDidMount() {
  //   this.fetchUsers();
  // }


  // startCreateUserHandler = () => {
  //   this.setState({ creating: true });
  //   console.log("CreateUserForm...");
  // };
  // startUpdateUserHandler = () => {
  //   this.setState({ updating: true });
  //   console.log("UpdateUserForm...");
  // };

  modalConfirmHandler = (event) => {

    event.preventDefault();
    console.log("signing up...");

    // console.log("CreateUserFormData:  ", event.target.formGridEmail.value);

    this.setState({ creating: false });
    const email = event.target.formGridEmail.value;
    const password = event.target.formGridPassword.value;
    const name = event.target.formGridName.value;
    const role = event.target.formGridRole.value;

    if (
      email.trim().length === 0 ||
      password.trim().length === 0 ||
      name.trim().length === 0 ||
      role.trim().length === 0
    ) {
      console.log("empty fields present!!!");
      this.state.success = "Empty fields present!!!"
      return;
    }

    const user = { email, password, name, role };
    console.log("creating user.. " + JSON.stringify(user));

    const requestBody = {
      query: `
          mutation CreateUser($email: String!, $password: String!, $name: String!, $role: String!) {
            createUser(userInput: {email: $email, password: $password, name: $name, role: $role}) {
              _id
              email
              password
              name
              role
            }
          }
        `,
        variables: {
          email: email,
          password: password,
          name: name,
          role: role
        }
    };

    const token = this.context.token;

    fetch('http://localhost:10000/graphql', {
      method: 'POST',
      body: JSON.stringify(requestBody),
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + token
      }
    })
      .then(res => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error('Failed!');
        }
        return res.json();
      })
      .then(resData => {
        console.log("response data... " + JSON.stringify(resData));
        console.log("resData head slice:  ", JSON.stringify(resData).slice(2,7));
        if (JSON.stringify(resData).slice(2,7) === 'error') {
          console.log("signup...failed...");
          this.state.success = "Something went wrong!";
        } else {
          this.state.success = "Signup Success... Proceed to Login";
          console.log(this.state.success);
        }
        return this.state.success;

      })
      .catch(err => {
        console.log(err);
      });
  };

  modalCancelHandler = () => {
    this.setState({ creating: false, updating: false, selectedUser: null });
  };


  // componentWillUnmount() {
  //   this.isActive = false;
  // }

  render() {
    return (
      <React.Fragment>
        { (
          <CreateUserForm
            canConfirm
            onConfirm={this.modalConfirmHandler}
            onSubmit={this.modalConfirmHandler}
            confirmText="Confirm"
            successText={this.state.success}
          />
        )
      }
      {this.state.success === true && (
        <Button variant="secondary" type="button" className="signupToLoginButton">
        <NavLink to="/auth">Login</NavLink>
        Login
        </Button>

      )}
      </React.Fragment>
    );
  }
}

export default SignupPage;
