import React from 'react';
import Button from 'react-bootstrap/Button';

import './UserItem.css';


const patientAllergiesItem = props => (
  <li key={props.userId} className="users__list-item">
    <div>
    <h5 className="userItemHeading"> Title:</h5>
    <p className="userItemText">
    {props.title}
    </p>
    <h5 className="userItemHeading"> Type:</h5>
    <p className="userItemText">
    {props.type}
    </p>
    <h5 className="userItemHeading"> Description:</h5>
    <p className="userItemText">
    {props.description}
    </p>
      <p>
        Description: {props.description}
      </p>
      <p>
        Attachment: {props.attachment.name}
      </p>
      <p>
        Attachment Format: {props.attachment.format}
      </p>
      <p>
        Attachment Path: {props.attachment.path}
      </p>
    </div>
    { props.canDelete === true && (
      <Button variant="danger" onClick={props.onDelete.bind(this, props.allergies)}>
        Delete
      </Button>
    )}
    <Button variant="info" onClick={props.onViewAttachment.bind(this, props.attachment)}>
      View
    </Button>
  </li>
);

export default patientAllergiesItem;
