import React from 'react';

import PatientVigilanceItem from './PatientItem/PatientVigilanceItem';
import './UserList.css';

const patientVigilanceList = props => {
  console.log("patient billing list props", props.patientVigilance);
  const patientVigilance = props.patientVigilance.map(billing => {
    const patientVigilanceDate = new Date(billing.date.substr(0,10)*1000).toISOString().slice(0,10);
    const attachmentLink = "https://ent-emr-bucket.s3-us-east-2.amazonaws.com/"+billing.attachment.path+"/"+billing.attachment.name+"."+billing.attachment.format;
    return (
      <PatientVigilanceItem
        key={billing.date}
        userId={props.authUserId}
        date={patientBillingDate}
        type={billing.type}
        title={billing.title}
        description={billing.description}
        amount={billing.amount}
        paid={billing.paid}
        notes={billing.notes}
        attachment={billing.attachment}
        canDelete={props.canDelete}
        onDelete={props.onDelete}
        billing={billing}
        onViewAttachment={props.onViewAttachment}
        attachmentLink={attachmentLink}
      />
    );
  });

  return <ul className="user__list1_detail">{patientVigilance}</ul>;
};

export default patientVigilanceList;
