import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

import './thisUserProfile.css';
// console.log("thisUserProfile.props.user:  " + {...user});
const thisUserProfile = (props) => {

  const {...user} = props.user;

  return (
    <Card className="UserDetailCard">
    <Card.Body>
      <Card.Title>User Details</Card.Title>
      <Card.Text>
        ID: {user._id}
      </Card.Text>
      <Card.Text>
        Name: {user.name}
      </Card.Text>
      <Card.Text>
        Role: {user.role}
      </Card.Text>
      <Card.Text>
        Start Date: {user.employmentDate}
      </Card.Text>
      <Card.Text>
        End Date: {user.terminationDate}
      </Card.Text>
      <Card.Text>
        Attendance:
      </Card.Text>
      {authSelectedUser.slice(0,5) === '{"_id'
         && (<UserAttendanceList
             userAttendance={userAttendance}
             authUserId={props.AuthContext.userId}
           />)}
      {authSelectedUser.slice(0,5) === '{"_id'
         && (<UserLeaveList
             userLeave={userLeave}
             authUserId={props.AuthContext.userId}
           />)}
      {authSelectedUser.slice(0,5) === '{"_id'
         && (<UserAttachmentList
             userAttachment={userAttachment}
             authUserId={props.AuthContext.userId}
           />)}
      <Button variant="link" className="btn" onClick={props.onEdit}>
      Edit
      </Button>

      <Button variant="warning" onClick={props.onDelete}>Delete</Button>
    </Card.Body>
  </Card>
  );
}

export default thisUserProfile;
