  fetchAppointments() {
    console.log("'fetch appointments function' context object... " + JSON.stringify(this.context));
    const userId = this.context.userId;
    this.setState({
      isLoading: true
    });
    const requestBody = {
      query: "\n          query appointments($userId: ID!) {\n            appointments(userId: $userId)\n            {\n              _id\n              title\n              type\n              date\n              time\n              location\n              description\n              patient\n              {\n                name\n                dob\n                address\n              }\n              inProgress\n              attended\n              important\n              notes\n            }\n          }\n        ",
      variables: {
        userId: userId
      }
    };
    fetch('http://localhost:10000/graphql', {
      method: 'POST',
      body: JSON.stringify(requestBody),
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + this.context.token
      }
    }).then(res => {
      if (res.status !== 200 && res.status !== 201) {
        throw new Error('Failed!');
      }

      return res.json();
    }).then(resData => {
      const appointments = resData.data.appointments;
      console.log(appointments);
      this.context.appointments = this.state.appointments;

      if (this.isActive) {
        this.setState({
          appointments: appointments,
          isLoading: false
        });
      }
    }).catch(err => {
      console.log(err);

      if (this.isActive) {
        this.setState({
          isLoading: false
        });
      }
    });
  }

