import React from 'react';
import Button from 'react-bootstrap/Button';
import { Page, Text, View, Document, StyleSheet } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';

import "./AttachmentViewer.css"

const styles = StyleSheet.create({
  page: {
    flexDirection: 'row',
    backgroundColor: '#E4E4E4'
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1
  }
});

let docProps = null;

const MyDocument = () => (
  <Document>
    <Page size="A4" style={styles.page}>
      <View style={styles.section}>
        <Text>This is a test document</Text>
        <Text>I could be a prescription, referral or the like.</Text>
        <Text>{docProps.pdfData.title}</Text>
        <Text>Patient Data: Name</Text>
        <Text>{docProps.pdfData.patient.name}</Text>
        <Text>Patient Data: Town</Text>
        <Text>{docProps.pdfData.patient.address.town}</Text>
        <Text>Appointment Data: Title</Text>
        <Text>{docProps.pdfData.appointment.title}</Text>
        <Text>Appointment Data: Tile</Text>
        <Text>{docProps.pdfData.appointment.time}</Text>

      </View>
      <View style={styles.section}>
        <Text>Here is some sample patient data</Text>
        <Text>Name: {docProps.pdfData.user.name}</Text>
        <Text>Phone: {docProps.pdfData.contatc.phone}</Text>
        <Text>Town: {docProps.pdfData.user.address.town}</Text>
        <Text>Allergies</Text>
        <Text>#1 type : {docProps.pdfData.user.allergies[1].type}</Text>
      </View>
    </Page>
  </Document>
);

const PdfCreator = (props) =>{
  console.log(`
    PdfCreator.props: ${JSON.stringify(props)},
    `);
    docProps = props;

return (
  <div className="attachmentViewerBg">
    <div className="attachmentViewer">
    <h5> Document Generator </h5>
    <Button variant="danger" onClick={props.onClosePdfCreator}>
      close
    </Button>


    </div>
    {
      <PDFViewer>
        <MyDocument />
      </PDFViewer>
    }
  </div>
)

}


export default PdfCreator;
